import React, {useState, useEffect}from 'react';
import { useParams } from 'react-router-dom';
import "./ProjectCarrousel.css";
import { ProjectService } from '../../services/ProjectService';


function ProjectCarrousel() {
    const { id } = useParams();
    const [projects, setProjects] = useState([]); // Agregamos estado para la lista completa de proyectos

    const api = ProjectService();

    useEffect(() =>{
        api.getAll().then(res => {
          console.log(res)
          setProjects(res.data);
         }).catch(error => console.log(error))
      },[])




  return (
    <>
    <div className="project-slider-full-container">

    <div className="previous-project-container">
        <button className = "slider-invisible-button">
        <svg xmlns="http://www.w3.org/2000/svg" width="10" height="16"><path fill="none" stroke="#33323D" d="M9 0L1 8l8 8"/></svg>
            <div className='slider-previous-button-text'></div>
            <h2>{}</h2>
            <p>Previous project</p>
        </button>
    </div>


    <div className="next-project-container">
    <button className = "slider-invisible-button"> 
    <svg xmlns="http://www.w3.org/2000/svg" width="10" height="16"><path fill="none" stroke="#33323D" d="M1 0l8 8-8 8"/></svg>

        <div className = "slider-next-button-text">
            <h2>{}</h2>
            <p>Next project</p>
            </div>
        </button>
    </div>


    </div>
    </>
  )
}

export default ProjectCarrousel
