import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { ProjectService } from '../services/ProjectService';
import ProjectInfo from '../components/ProjectInfo/ProjectInfo';
import Layout from '../components/Layout';
import ProjectCarrousel from '../components/ProjectCarrousel/ProjectCarrousel';



function ProjectDetail() {
    const {id} = useParams();
    const [project, setProject] = useState({});
    const [projects, setProjects] = useState([]); // Agregamos estado para la lista completa de proyectos


    useEffect(() => {
        const api = ProjectService();

        api.getAll().then(res => {
          console.log(res)
          setProjects(res.data);
         }).catch(error => {
          console.log(error);
        });

        api.getById(id)
            .then (response => {
                setProject(response.data);
            })
            .catch(error => {
              console.log(error);
            });




    }, [id]);

    console.log(project)






  return (
    <>
    <Layout>

         <ProjectInfo project={project} />
        <ProjectCarrousel projects={projects} currentProject={project} />

    </Layout>
    </>
  )
}

export default ProjectDetail
