import React, {useState} from 'react';

import "./ProjectCarrousel.css";



function ProjectCarrousel({projects, currentProject}) {
    // const [currentProject, setCurrentProject] = useState([]);
    const currentProjectIndex = projects.findIndex(p => p.id === currentProject.id);
    const previousProject = projects[currentProjectIndex - 1];
    const nextProject = projects[currentProjectIndex + 1];


    const goToPreviousProject = () => {
        // Lógica para ir al proyecto anterior
        // Puedes implementar una función en ProjectDetail para actualizar el proyecto actual
        setCurrentProject(previousProject);
    };

      const goToNextProject = () => {
        // Lógica para ir al próximo proyecto
        // Puedes implementar una función en ProjectDetail para actualizar el proyecto actual
        setCurrentProject(nextProject);
    };




  return (
    <>
    <div className="project-slider-full-container">

    <div className="previous-project-container">
    {previousProject && (
            <button className="slider-invisible-button" onClick={goToPreviousProject}>
              <svg xmlns="http://www.w3.org/2000/svg" width="10" height="16">
                <path fill="none" stroke="#33323D" d="M9 0L1 8l8 8"/>
              </svg>
              <div className='slider-previous-button-text'>
                <h2>{previousProject.title}</h2>
                <p>Previous project</p>
              </div>
            </button>
          )}
    </div>


    <div className="next-project-container">
    {nextProject && (
            <button className="slider-invisible-button" onClick={goToNextProject}>
              <svg xmlns="http://www.w3.org/2000/svg" width="10" height="16">
                <path fill="none" stroke="#33323D" d="M1 0l8 8-8 8"/>
              </svg>
              <div className="slider-next-button-text">
                <h2>{nextProject.title}</h2>
                <p>Next project</p>
              </div>
            </button>
          )}
    </div>


    </div>
    </>
  )
}

export default ProjectCarrousel
